import React from 'react';
import ReactDOM from 'react-dom';

class BMICalculator extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            gender: 'male',
            weight: '',
            height: '',
            bmiResult: null,
        };
    }

    calculateBMI() {
        const { weight, height, gender } = this.state;
        if (isNaN(weight) || isNaN(height) || weight <= 0 || height <= 0) {
            alert('Please enter valid weight and height.');
            return;
        }

        let bmi;
        if (gender === 'male') {
            bmi = weight / Math.pow(height / 100, 2);
        } else {
            // Use different coefficients for females
            bmi = 0.9 * (weight / Math.pow(height / 100, 2));
        }

        this.setState({ bmiResult: bmi });
    }

    render() {
        const { gender, weight, height, bmiResult } = this.state;

        let resultText = '';
        if (bmiResult !== null) {
            if (bmiResult < 18.5) {
                resultText = 'Weight is insufficient. There is an increased risk of health disorders, such as amenorrhea in women (absence of menstruation), brittle bones, and a lack of nutrients for the healthy functioning of all body systems.';
            } else if (bmiResult >= 18.5 && bmiResult <= 24.9) {
                resultText = 'Weight is normal. The ideal BMI for people under 25 years old is 22-23. If BMI approaches 25, start monitoring waist circumference and remember about physical activity.';
            } else if (bmiResult >= 25 && bmiResult <= 29.9) {
                resultText = 'Overweight. There is an increased risk of cardiovascular diseases, diabetes, sexual dysfunction, and other illnesses. Try to lose weight, start eating healthy, and engage in sports.';
            } else {
                resultText = 'Obesity. You need to lose weight to lower BMI values for a healthy life. A high BMI is associated with an increased risk of mortality from any cause, including diabetes, cardiovascular diseases, hypertension, and arthritis.';
            }
        }

        return (
            <div id="root">
                <h1>Body Mass Index (BMI) Calculator</h1>
                <form id="bmiForm">
                    <label>
                        Gender:
                        <select value={gender} onChange={(e) => this.setState({ gender: e.target.value })}>
                            <option value="male">Male</option>
                            <option value="female">Female</option>
                        </select>
                    </label>
                    <br />
                    <label>
                        Weight (kg):
                        <input type="number" value={weight} onChange={(e) => this.setState({ weight: e.target.value })} required />
                    </label>
                    <br />
                    <label>
                        Height (cm):
                        <input type="number" value={height} onChange={(e) => this.setState({ height: e.target.value })} required />
                    </label>
                    <br />
                    <button type="button" onClick={() => this.calculateBMI()}>Calculate BMI</button>
                </form>
                <h2>Result:</h2>
                <p id="result">
                    {bmiResult !== null && (
                        <React.Fragment>
                            Your BMI: {bmiResult.toFixed(2)}
                            <br />
                            {resultText}
                        </React.Fragment>
                    )}
                </p>
            </div>
        );
    }
}

ReactDOM.render(<BMICalculator />, document.getElementById('root'));

