const root = document.getElementById('root');

const h1 = document.createElement('h1');
h1.textContent = 'Body Mass Index (BMI) Calculator';

const form = document.createElement('form');
form.id = 'bmiForm';

const genderLabel = document.createElement('label');
genderLabel.textContent = 'Gender:';
const genderSelect = document.createElement('select');
genderSelect.value = 'male';
genderSelect.addEventListener('change', (e) => {
  genderSelect.value = e.target.value;
});
const maleOption = document.createElement('option');
maleOption.value = 'male';
maleOption.textContent = 'Male';
const femaleOption = document.createElement('option');
femaleOption.value = 'female';
femaleOption.textContent = 'Female';
genderSelect.appendChild(maleOption);
genderSelect.appendChild(femaleOption);
genderLabel.appendChild(genderSelect);

const weightLabel = document.createElement('label');
weightLabel.textContent = 'Weight (kg):';
const weightInput = document.createElement('input');
weightInput.type = 'number';
weightInput.addEventListener('input', (e) => {
  weightInput.value = e.target.value;
});
weightLabel.appendChild(weightInput);

const heightLabel = document.createElement('label');
heightLabel.textContent = 'Height (cm):';
const heightInput = document.createElement('input');
heightInput.type = 'number';
heightInput.addEventListener('input', (e) => {
  heightInput.value = e.target.value;
});
heightLabel.appendChild(heightInput);

const calculateButton = document.createElement('button');
calculateButton.type = 'button';
calculateButton.textContent = 'Calculate BMI';
calculateButton.addEventListener('click', () => calculateBMI());

form.appendChild(genderLabel);
form.appendChild(document.createElement('br'));
form.appendChild(weightLabel);
form.appendChild(document.createElement('br'));
form.appendChild(heightLabel);
form.appendChild(document.createElement('br'));
form.appendChild(calculateButton);

const h2 = document.createElement('h2');
h2.textContent = 'Result:';
const resultParagraph = document.createElement('p');
resultParagraph.id = 'result';

root.appendChild(h1);
root.appendChild(form);
root.appendChild(h2);
root.appendChild(resultParagraph);

function calculateBMI() {
  const weight = parseFloat(weightInput.value);
  const height = parseFloat(heightInput.value);
  const gender = genderSelect.value;

  if (isNaN(weight) || isNaN(height) || weight <= 0 || height <= 0) {
    alert('Please enter valid weight and height.');
    return;
  }

  let bmi;

  if (gender === 'male') {
    bmi = weight / Math.pow(height / 100, 2);
  } else {
    bmi = 0.9 * (weight / Math.pow(height / 100, 2));
  }

  resultParagraph.textContent = `Your BMI: ${bmi.toFixed(2)}`;
}
